mapper.update:
update student
set username = #{username}, password = #{password}, name = #{name}, avatar = #{avatar}, role = #{role}, sex = #{sex}, code = #{code},
    college_id = #{collegeId}, speciality_id = #{specialityId}, score = #{score}
where id = #{id}

mapper.select:
select student.*, college.name as collegeName, speciality.name as specialityName from student left join college on student.college_id = college.id left join speciality on student.speciality_id = speciality.id
student.name like concat('%', #{name}, '%')
and student.college_id = #{collegeId}
and student.speciality_id = #{specialityId}
order by student.id desc
